sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var rec = inputs.records;
  var calc = 0;

  while(rec.next())
    //calc = parseInt(calc) + parseInt(rec.net_order_value_usd);
    calc = calc + rec.net_order_value_usd;

  outputs.totalvalue = calc;

})(inputs, outputs);

