sys_ux_client_script.script:
      function handler({api, event, imports}) {
    const {NowDateTime} = imports['sn_uxf.core_libraries']();

	  var start = NowDateTime(event.payload.startMoment.startOf("week"));
    var end = NowDateTime(event.payload.endMoment.endOf("week"));
    const futureBookableDays = api.data.appointment_booking_config_1.output ? api.data.appointment_booking_config_1.output.future_bookable_max_days: 14;

    if (start.isBefore(NowDateTime())) {
      start = NowDateTime().startOf('day');
    }

      if (end.isAfter(NowDateTime().add(futureBookableDays, 'days'))) {
      end = NowDateTime().endOf('day').add(futureBookableDays, 'days');
    }
    api.setState("start_date", start.format("YYYY-MM-DD HH:mm:ss"));
    api.setState("end_date", end.format("YYYY-MM-DD HH:mm:ss")); 
}
