sys_ux_client_script.script:
function script({
                    api,
                    helpers,
                    event,
                    imports
                }) {
                    const {
                        payload
                    } = event;
                    var redirectPayload = {
                        redirect: true,
                        targetRoute: 'current',
                        fields: {
                            sysId: payload.sys_id + "",
                            table: payload.table + ""
                        },
                        params: {
                            query: payload.params.query+""
                        }
                    };
                if(payload.sys_id == '-1') {
                redirectPayload.route = "record";
                } 
                else {
                redirectPayload.route = "kb_view";
                }
                    api.emit("NAV_ITEM_SELECTED", redirectPayload);
                }
