sys_ux_client_script.script:
      /**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {  
    api.setState("loading", false);

    if(api.data.appointment_booking_availability_1.output.result.next_available_slot) {
      api.setState("fetch_next_available_slot", false);

      var nextAvailableSlot = {
          start: api.data.appointment_booking_availability_1.output.result.next_available_slot.start_date,
          end: api.data.appointment_booking_availability_1.output.result.next_available_slot.end_date,
          type: "available"
      };

      api.setState("next_available_slot", nextAvailableSlot); 
  }
}
