sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
async function handler({api, event, helpers, imports}) {
    switch(event.payload.item.id) {
        case 'nam_kpis':
            api.setState('showNewModelKPIs', true);
            api.setState('kpiCategoryList', [{"id":"all","label":await helpers.translate("All")},{"id":"created_by_me","label":await helpers.translate("created_by_me_lowercase")}]);
            break;
        case 'classic_kpis':
        default:
            api.setState('showNewModelKPIs', false);
            api.setState('kpiCategoryList', [{"id":"all","label":await helpers.translate("All")},{"id":"bookmarked","label":await helpers.translate("Bookmarked")},{"id":"created_by_me","label":await helpers.translate("created_by_me_lowercase")}]);
            break;
    }
}
