sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const fields = {
        table: api.state.selected_value.data.table,
        sysId: -1
    };
    const params = {
        query: api.state.selected_value.data.query
    };
    var title = api.state.selected_value.tabTitle;
    helpers.navigate.to('record', fields, params, false, false, 'current', null, title);
    api.setState('selected_value', {});
    helpers.modal.close('select_case_or_task_model');
}
