sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    if (api.context.props.parentTable == 'sn_shop_purchase_line' && api.context.props.table == 'sn_spend_sdc_service_task')
        api.setState('finalQuery', 'spend_purchase_requisition=' + api.data.look_up_record_1.result.spend_purchase_requisition.value + '^purchase_line=' + api.context.props.query);
    else if (api.context.props.parentTable == 'sn_shop_purchase_line' && api.context.props.table == 'sn_spend_sdc_service_request')
        api.setState('finalQuery', 'purchase_request=' + api.data.look_up_record_1.result.spend_purchase_requisition.value + '^purchase_line=' + api.context.props.query);
}
