sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    const { table } = api.context.props;
    if (api.state.tablesToDisableAddButton) {
        const tableList =api.state.tablesToDisableAddButton;
        if (tableList.includes(table)) {
            const state = api.context.props.fields.state.displayValue;
            if (state.toLowerCase().includes('closed'))
                return true;
        }
    }
    return false;
}
