sys_ux_client_script.script:
      /**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  const timezone = api.data.appointment_booking_availability_transform_1.output.timezone;
  const config = JSON.parse(api.data.appointment_booking_config_1.output.config);
  const service_config = config.data.service_config;
  const showArriveByMessage = service_config.use_slot_end_time_as == "arrive_by";
  const arriveByText = api.state.translatedArriveByMessage;

	return '<div style="font-size: 13px; font-weight: 400;color: RGB(var(--now-color_text--primary,var(--now-color--neutral-18,22,27,28)));">' + 
          '<div style="padding: 2px">Time Zone: ' + timezone + '</div>' +
          (showArriveByMessage ? ('<now-icon style="color: RGB(var(--now-color--primary-1,30,133,109)); display: inline;" slot="default-slot" icon="circle-info-outline" dir="ltr"></now-icon> <span>' + arriveByText + '</span>') : "") +
         '</div>';
}
